import React from 'react';
import styled from 'styled-components';
import { BaseContainer } from '../../helpers/layout';
import { api, handleError } from '../../helpers/api';
import Player from '../../views/Player';
import { Spinner } from '../../views/design/Spinner';
import { Button } from '../../views/design/Button';
import { withRouter } from 'react-router-dom';
import User from "../shared/models/User";
import Profile from "../../views/Profile";

const Container = styled(BaseContainer)`
  color: white;
  text-align: center;
`;

class UserProfile extends React.Component {
    constructor() {
        super();
        this.state = {
            userId: localStorage.getItem("id"),
            userToBeShown: null
        };
        this.getUser(this.state.userId);
    }

    async getUser(id) {
        try {
            let userId = id;
            const response = await api.get('/users/${userId}');

            const user = new User(response.data);

            this.setState({userToBeShown: user});
        } catch (e) {
            alert(`Something went wrong while fetching the user: \n${handleError(e)}`);
        }
    }

    returnToDashboard(){
        this.setState({userId: null});
        this.setState({userToBeShown: null});
        this.props.history.push('/dashboard');
    }

    async componentDidMount() {}

    render() {
        return (
            <Container>
                <h2>User: {this.state.userToBeShown ? this.state.userToBeShown.username : null} </h2>
                {this.state.userToBeShown ? (
                    <Profile user={this.state.userToBeShown}/>
                ) : null}
                <div>
                    <Button
                        width="100%"
                        onClick={() => {
                            this.returnToDashboard();
                        }}
                    >
                        Return to the Dashboard
                    </Button>
                </div>
            </Container>
        );
    }
}

export default withRouter(UserProfile);
