import React from 'react';
import styled from 'styled-components';
import {BaseContainer} from '../../helpers/layout';
import {api, handleError} from '../../helpers/api';
//import Player from '../../views/Player';
//import { Spinner } from '../../views/design/Spinner';
import {Button} from '../../views/design/Button';
import {withRouter} from 'react-router-dom';
import User from "../shared/models/User";
import Profile from "../../views/Profile";
import EditProfile from "../editprofile/EditProfile";

const Container = styled(BaseContainer)`
  color: white;
  text-align: center;
`;

class UserProfile extends React.Component {
    constructor() {
        super();
        this.state = {
            loggedInUserId: localStorage.getItem("id"),
            userToBeShownId: localStorage.getItem("showUserId"),
            userToBeShown: null
        };
    }

    async getUser(id) {
        try {
            const response = await api.get('/users', {params: {userId: id}});

            const user = new User(response.data);

            this.setState({userToBeShown: user});
        } catch (e) {
            alert(`Something went wrong while displaying the user profile: \n${handleError(e)}`);
        }
    }

    editProfile(user) {
        EditProfile.setState({ user: user });
        this.props.history.push('/game/dashboard/user/edit');
    }

    returnToDashboard(){
        this.setState({userToBeShownId: null});
        this.setState({userToBeShown: null});
        this.props.history.push('/game/dashboard');
    }

    async componentDidMount() {
        this.getUser(this.state.userToBeShownId);
    }

    render() {
        return (
            <BaseContainer>
                <Container>
                    <h2>User: {this.state.userToBeShown ? this.state.userToBeShown.username : null} </h2>
                    {this.state.userToBeShown ? (
                        <Profile user={this.state.userToBeShown}/>
                    ) : null}
                    <div>
                    { this.state.loggedInUserId === this.state.userToBeShownId ?
                        <Button onClick={() => {this.editProfile(this.state.userToBeShown)}}>
                            Edit your profile
                        </Button>
                        : null }
                    </div>
                    <div>
                        <Button
                            width="100%"
                            onClick={() => {
                                this.returnToDashboard();
                            }}
                        >
                            Return to the Dashboard
                        </Button>
                    </div>
                </Container>
            </BaseContainer>
        );
    }
}

export default withRouter(UserProfile);
