import React from 'react';
import styled from 'styled-components';
import { BaseContainer } from '../../helpers/layout';
import { api, handleError } from '../../helpers/api';
import Player from '../../views/Player';
import { Spinner } from '../../views/design/Spinner';
import { Button } from '../../views/design/Button';
import { withRouter } from 'react-router-dom';
import Profile from "../../views/Profile";

const Container = styled(BaseContainer)`
  color: white;
  text-align: center;
`;

const Users = styled.ul`
  list-style: none;
  padding-left: 0;
`;

const PlayerContainer = styled.li`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Label = styled.label`
  color: white;
  margin-bottom: 10px;
  text-transform: uppercase;
`;

const InputField = styled.input`
  &::placeholder {
    color: rgba(255, 255, 255, 1.0);
  }
  height: 35px;
  padding-left: 15px;
  margin-left: -4px;
  border: none;
  border-radius: 20px;
  margin-bottom: 20px;
  background: rgba(255, 255, 255, 0.2);
  color: white;
`;

class EditProfile extends React.Component {
    constructor() {
        super();
        this.state = {
            loggedInUserId: localStorage.getItem("id"),
            newUsername: null,
            newBirthDate: null
        };
    }

    async saveChanges() {
        try {
            const postBody = JSON.stringify({
                username: this.state.newUsername,
                birthDate: this.state.newBirthDate
            });
            await api.post('/users/{id}', postBody);

        } catch (e) {
            alert(`Something went wrong while fetching the user: \n${handleError(e)}`);
        }
    }

    handleInputChange(key, value) {
        // Example: if the key is username, this statement is the equivalent to the following one:
        // this.setState({'username': value});
        this.setState({ [key]: value });
    }

    async componentDidMount() {}

    render() {
        return (
            <BaseContainer>
                <Container>
                    <h2>User: {this.state.userToBeShown ? this.state.userToBeShown.username : null} </h2>
                    <Label>Username</Label>
                    <InputField
                        placeholder="Enter your new username here"
                        onChange={e => {
                            this.handleInputChange('newUsername', e.target.value);
                        }}
                    />
                    <Label>BirthDate</Label>
                    <InputField
                        placeholder="Enter your birth date here"
                        onChange={e => {
                            this.handleInputChange('newBirthDate', e.target.value);
                        }}
                    />
                    <div>
                        <Button
                            width="100%"
                            onClick={() => {
                                this.saveChanges();
                            }}
                        >
                            Save your changes
                        </Button>
                    </div>
                    <div>
                        <Button
                            width="100%"
                            onClick={() => {
                                this.returnToDashboard();
                            }}
                        >
                            Return to the Dashboard
                        </Button>
                    </div>
                </Container>
            </BaseContainer>
        );
    }
}

export default withRouter(EditProfile);
